import React, { useState, useRef, useEffect } from 'react';
import { Canvas, Image, util } from 'fabric';

const DocumentCanvas = ({ documentUrl, sessionId }) => {
  const canvasRef = useRef(null);
  const [canvas, setCanvas] = useState(null);
  const socket = useRef(null); // WebSocket connection

  useEffect(() => {
    console.log('Initializing canvas...');
    // Create a Fabric.js canvas instance
    const canvasInstance = new Canvas(canvasRef.current);
    setCanvas(canvasInstance);

    // Load the background image
    if (documentUrl) {
      Image.fromURL(
        documentUrl,
        (img) => {
          canvasInstance.setBackgroundImage(
            img,
            canvasInstance.renderAll.bind(canvasInstance),
            {
              crossOrigin: 'anonymous', // Prevent cross-origin issues
            }
          );
          console.log('Document URL:', documentUrl);
        },
        { crossOrigin: 'anonymous' }
      );
    } else {
      console.warn('No document URL provided.');
    }

    // Set up WebSocket connection
    socket.current = new WebSocket(`ws://localhost:8000/ws/session/${sessionId}/`);
    
    socket.current.onopen = () => {
      console.log('WebSocket connection established.');
    };

    socket.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.type === 'draw') {
        console.log('Received draw data:', data.payload);
        util.enlivenObjects([data.payload], (objects) => {
          objects.forEach((obj) => {
            if (canvasInstance) {
              canvasInstance.add(obj);
            }
          });
        });
      }
    };

    socket.current.onclose = () => {
      console.log('WebSocket connection closed.');
    };

    socket.current.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    // Emit data when an object is added to the canvas
    const emitObjectData = (object) => {
      try {
        if (socket.current) {
          socket.current.send(
            JSON.stringify({
              type: 'draw',
              payload: object.toJSON(),
            })
          );
          console.log('Object emitted:', object);
        }
      } catch (error) {
        console.error('Error emitting object data:', error.message);
      }
    };

    canvasInstance.on('object:added', (e) => {
      const object = e.target;
      emitObjectData(object);
    });

    // Cleanup on component unmount
    return () => {
      canvasInstance.dispose();
      if (socket.current) {
        socket.current.close();
      }
    };
  }, [documentUrl, sessionId]);

  return (
    <div>
      <canvas ref={canvasRef} width={800} height={600} />
      {!documentUrl && <p style={{ color: 'red' }}>No document available to load.</p>}
    </div>
  );
};

export default DocumentCanvas;
