import React, { useState, useEffect } from 'react';
import DocumentCanvas from './DocumentCanvas';
import AudioChat from './AudioChat';
import { useSession } from './SessionContext';

const SessionPage = () => {
  const { sessionId, documentUrl, quiz_id } = useSession(); // Still accessing session data for validation
  const [error, setError] = useState('');
  
  useEffect(() => {
    if (!sessionId) {
      setError("Error: Session ID is undefined.");
    } else if (!documentUrl) {
      setError("Error: No document URL available for this session.");
    } else {
      setError(''); // Clear errors if both sessionId and documentUrl are valid
    }
  }, [sessionId, documentUrl]);

  if (error) {
    return <p style={{ color: 'red' }}>{error}</p>;
  }

  return (
    <div>
      <h1>Session Page</h1>
      <DocumentCanvas /> {/* No need to pass props as useSession is used internally */}
      <AudioChat  /> {/* Pass sessionId here if AudioChat depends on it */}
      <a href={`http://127.0.0.1:8000/attempt-quiz/${quiz_id}/`}>Go to Quiz</a>
    </div>
  );
};

export default SessionPage;
