import React, { useState, useEffect } from 'react';

const FetchSessionData = ({ onSessionData }) => {
  const [loading, setLoading] = useState(true); // Manage the loading state
  const [error, setError] = useState(null); // Manage error messages

  useEffect(() => {
    // Check if the onSessionData prop is a valid function
    if (typeof onSessionData !== 'function') {
      console.error('onSessionData prop is required and must be a function.');
      return;
    }

    // Fetch session data from the backend
    const fetchData = async () => {
      try {
        console.log('Fetching session data...');
        
        const response = await fetch('http://127.0.0.1:8000/api/get-session-id/', {
          credentials: 'include', // Include authentication credentials
        });

        if (!response.ok) {
          throw new Error(`Failed to fetch session data: ${response.status}`);
        }

        const data = await response.json(); // Parse JSON response

        if (!data || !data.id) {
          throw new Error(`Session ID is missing in the response. Data: ${JSON.stringify(data)}`);
        }

        console.log('Fetched session data:', data);

        // Pass the fetched data to the parent component
        onSessionData(data);

        // Reset error if previously set
        setError(null);
      } catch (err) {
        let errorMessage = 'An unknown error occurred.';

        // Format the error message
        if (err instanceof Error) {
          errorMessage = err.message;
        } else if (typeof err === 'string') {
          errorMessage = err;
        } else if (err && typeof err === 'object') {
          errorMessage = err.error || JSON.stringify(err);
        }

        console.error('Error fetching session data:', errorMessage);
        setError(errorMessage);
      } finally {
        // Stop the loading state
        setLoading(false);
      }
    };

    // Call the fetchData function
    fetchData();
  }, [onSessionData]);

  // Render loading state
  if (loading) {
    return <p>Loading session data...</p>;
  }

  // Render error state
  if (error) {
    return (
      <div style={{ color: 'red', padding: '10px', border: '1px solid red' }}>
        <p>Error: {error}</p>
        <button onClick={() => window.location.reload()}>Retry</button>
      </div>
    );
  }

  // Return null because this component's primary purpose is to fetch data
  return null;
};

export default FetchSessionData;
