import React, { useState, useRef, useEffect } from 'react';
import io from 'socket.io-client';
import { fabric } from 'fabric';
import { getDocument } from 'pdfjs-dist';

const DocumentCanvas = ({ documentUrl }) => {
  const canvasRef = useRef(null);
  const [canvas, setCanvas] = useState(null);

  useEffect(() => {
    console.log('Initializing canvas...');
    const canvasInstance = new fabric.Canvas(canvasRef.current);
    setCanvas(canvasInstance);

    // Handle PDF documents
    if (documentUrl.endsWith('.pdf')) {
      const loadingTask = getDocument(documentUrl);
      loadingTask.promise
        .then((pdf) => {
          console.log(`PDF loaded with ${pdf.numPages} pages.`);
          return pdf.getPage(1);
        })
        .then((page) => {
          const viewport = page.getViewport({ scale: 1.5 });
          const canvasElement = document.createElement('canvas');
          const context = canvasElement.getContext('2d');
          canvasElement.width = viewport.width;
          canvasElement.height = viewport.height;

          const renderTask = page.render({
            canvasContext: context,
            viewport: viewport,
          });

          return renderTask.promise.then(() => {
            const imageUrl = canvasElement.toDataURL();
            fabric.Image.fromURL(imageUrl, (img) => {
              canvasInstance.setBackgroundImage(img, canvasInstance.renderAll.bind(canvasInstance));
              console.log('PDF page rendered as background image.');
            });
          });
        })
        .catch((error) => {
          console.error('Error loading PDF:', error);
        });
    } else {
      console.warn('Unsupported document format or missing document URL.');
    }

    // WebSocket setup
    const socket = io('http://localhost:3000');
    socket.on('connect', () => {
      console.log('Connected to WebSocket server.');
    });

    socket.on('draw', (data) => {
      console.log('Received draw data:', data);
      fabric.util.enlivenObjects([data], (objects) => {
        objects.forEach((obj) => canvasInstance.add(obj));
      });
    });

    canvasInstance.on('object:added', (e) => {
      const object = e.target;
      socket.emit('draw', object.toJSON());
      console.log('Object added:', object);
    });

    return () => {
      canvasInstance.dispose();
      socket.disconnect();
    };
  }, [documentUrl]);

  return <canvas ref={canvasRef} width={800} height={600} />;
};

export default DocumentCanvas;
