import React, { useState, useEffect } from 'react';
import DocumentCanvas from './DocumentCanvas';
import AudioChat from './AudioChat';

const SessionPage = ({ documentUrl, sessionId }) => {
  const [uploadedDocument, setUploadedDocument] = useState(null);
  const [error, setError] = useState('');

  const handleDocumentUpload = (event) => {
    const file = event.target.files[0];
    if (!file) {
      setError('No file selected.');
      return;
    }

    // Validate file type
    if (file.type !== 'application/pdf' && !file.type.startsWith('image/')) {
      setError('Invalid file type. Please upload a PDF or image file.');
      return;
    }

    const reader = new FileReader();
    reader.onload = (e) => {
      setUploadedDocument(e.target.result);
      console.log('Document uploaded:', e.target.result);
    };
    reader.readAsDataURL(file);
  };

  useEffect(() => {
    console.log('SessionPage rendered with sessionId:', sessionId);
    console.log('Document URL:', documentUrl);
  }, [sessionId, documentUrl]);

  if (!sessionId) {
    return <p style={{ color: 'red' }}>Error: Session ID is undefined. Unable to proceed.</p>;
  }

  return (
    <div>
      <h1>Session Page</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <input type="file" onChange={handleDocumentUpload} />
      <DocumentCanvas documentUrl={uploadedDocument || documentUrl} sessionId={sessionId} />
      <AudioChat sessionId={sessionId} />
    </div>
  );
};

export default SessionPage;
