import React, { useRef, useEffect } from 'react';
import io from 'socket.io-client';

const AudioChat = ({ sessionId }) => {
  const audioRef = useRef(null);
  const peerConnections = {};
  const config = { iceServers: [{ urls: 'stun:stun.l.google.com:19302' }] };
  const socket = io('http://localhost:8080'); // Your server URL

  useEffect(() => {
    socket.on('offer', handleOffer);
    socket.on('answer', handleAnswer);
    socket.on('ice-candidate', handleNewICECandidateMsg);

    navigator.mediaDevices.getUserMedia({ audio: true })
      .then(stream => {
        audioRef.current.srcObject = stream;
        socket.emit('join', sessionId);
      });

    return () => {
      socket.disconnect();
    };
  }, [handleAnswersessionId]);

  const handleOffer = (offer) => {
    const peerConnection = new RTCPeerConnection(config);
    peerConnections[offer.sender] = peerConnection;
    peerConnection.setRemoteDescription(new RTCSessionDescription(offer));
    navigator.mediaDevices.getUserMedia({ audio: true })
      .then(stream => {
        stream.getTracks().forEach(track => peerConnection.addTrack(track, stream));
      });
    peerConnection.createAnswer()
      .then(answer => {
        peerConnection.setLocalDescription(answer);
        socket.emit('answer', { type: 'answer', sdp: answer, sender: socket.id });
      });
    peerConnection.ontrack = handleTrackEvent;
    peerConnection.onicecandidate = handleICECandidateEvent;
  };

  const handleAnswer = (answer) => {
    const peerConnection = peerConnections[answer.sender];
    peerConnection.setRemoteDescription(new RTCSessionDescription(answer));
  };

  const handleNewICECandidateMsg = (msg) => {
    const candidate = new RTCIceCandidate(msg.candidate);
    const peerConnection = peerConnections[msg.sender];
    peerConnection.addIceCandidate(candidate);
  };

  const handleTrackEvent = (event) => {
    audioRef.current.srcObject = event.streams[0];
  };

  const handleICECandidateEvent = (event) => {
    if (event.candidate) {
      socket.emit('ice-candidate', { type: 'candidate', candidate: event.candidate, sender: socket.id });
    }
  };

  return <audio ref={audioRef} autoPlay />;
};

export default AudioChat;
