import React, { useState, useRef, useEffect } from 'react';
import io from 'socket.io-client';
import { Canvas, Image, util } from 'fabric';

const DocumentCanvas = ({ documentUrl }) => {
  const canvasRef = useRef(null);
  // eslint-disable-next-line no-unused-vars
  const [canvas, setCanvas] = useState(null);

  useEffect(() => {
    console.log('Initializing canvas...');
    // Create a Fabric.js canvas instance
    const canvasInstance = new Canvas(canvasRef.current);
    setCanvas(canvasInstance);

    // Load the background image
    Image.fromURL(documentUrl, (img) => {
      canvasInstance.setBackgroundImage(img, canvasInstance.renderAll.bind(canvasInstance));
      console.log('Document URL:', documentUrl);
    });

    // Set up WebSocket connection
    const socket = io('http://localhost:8080');
    socket.on('connect', () => {
      console.log('Connected to WebSocket server.');
    });

    // Listen for drawing data from the server
    socket.on('draw', (data) => {
      console.log('Received draw data:', data);
      util.enlivenObjects([data], (objects) => {
        objects.forEach((obj) => canvasInstance.add(obj));
      });
    });

    // Emit data when an object is added to the canvas
    canvasInstance.on('object:added', (e) => {
      const object = e.target;
      socket.emit('draw', object.toJSON());
      console.log('Object added:', object);
    });

    // Clean up on component unmount
    return () => {
      canvasInstance.dispose();
      socket.disconnect();
    };
  }, [documentUrl]);

  return <canvas ref={canvasRef} width={800} height={600} />;
};

export default DocumentCanvas;
