import React, { useState, useRef, useLayoutEffect } from "react";
import * as pdfjs from "pdfjs-dist"; // Import for PDF.js
import { useSession } from "./SessionContext"; // Import for session context

// Fabric.js (CommonJS import)
const fabric = require("fabric").fabric;

// Set the worker source for PDF.js
pdfjs.GlobalWorkerOptions.workerSrc = "/pdf.worker.mjs"; // Ensure this is correctly set up

const DocumentCanvas = () => {
  const { sessionId, documentUrl } = useSession(); // Access sessionId and documentUrl from the session context
  const canvasRef = useRef(null);
  const socket = useRef(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);

  useLayoutEffect(() => {
    if (!canvasRef.current) {
      console.warn("Canvas reference is not ready yet.");
      return;
    }

    if (!sessionId) {
      console.error("Session ID is missing. WebSocket connection cannot be established.");
      return;
    }

    let canvasInstance;

    // Initialize Fabric.js canvas
    try {
      canvasInstance = new fabric.Canvas(canvasRef.current, {
        width: 800,
        height: 600,
      });
      console.log("Fabric.js canvas initialized.");
    } catch (error) {
      console.error("Error initializing Fabric.js canvas:", error);
      return;
    }

    const renderPDFToCanvas = async (pageNumber) => {
      if (!documentUrl) {
        console.warn("No document URL provided.");
        return;
      }

      try {
        const pdf = await pdfjs.getDocument(documentUrl).promise;
        setTotalPages(pdf.numPages);

        const page = await pdf.getPage(pageNumber);
        const viewport = page.getViewport({ scale: 1.5 });

        const pdfCanvas = document.createElement("canvas");
        pdfCanvas.width = viewport.width;
        pdfCanvas.height = viewport.height;

        const context = pdfCanvas.getContext("2d");
        await page.render({ canvasContext: context, viewport }).promise;

        // Set PDF page as the background for the Fabric.js canvas
        fabric.Image.fromURL(pdfCanvas.toDataURL(), (oImg) => {
          canvasInstance.setBackgroundImage(oImg, canvasInstance.renderAll.bind(canvasInstance));
          console.log(`Rendered page ${pageNumber} as background image.`);
        });
      } catch (error) {
        console.error("Error rendering PDF:", error);
      }
    };

    renderPDFToCanvas(currentPage);

    const connectWebSocket = () => {
      const webSocketUrl = `ws://127.0.0.1:8080/ws/session/${sessionId}/`;
      console.log("Attempting to connect to WebSocket:", webSocketUrl);

      socket.current = new WebSocket(webSocketUrl);

      socket.current.onopen = () => {
        console.log("WebSocket connection established.");
      };

      socket.current.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === "draw") {
          console.log("Received drawing data:", data.payload);
          fabric.util.enlivenObjects([data.payload], (objects) => {
            objects.forEach((obj) => {
              canvasInstance.add(obj);
            });
          });
        }
      };

      socket.current.onclose = () => {
        console.warn("WebSocket connection closed.");
      };

      socket.current.onerror = (error) => {
        console.error("WebSocket error:", error.message);
      };
    };

    connectWebSocket();

    return () => {
      console.log("Cleaning up resources...");
      canvasInstance.dispose();
      if (socket.current) {
        socket.current.close();
      }
    };
  }, [currentPage, documentUrl, sessionId]);

  const goToNextPage = () => {
    if (currentPage < totalPages) setCurrentPage((prevPage) => prevPage + 1);
  };

  const goToPreviousPage = () => {
    if (currentPage > 1) setCurrentPage((prevPage) => prevPage - 1);
  };

  return (
    <div>
      <canvas ref={canvasRef} />
      {!documentUrl && (
        <p style={{ color: "red" }}>
          No document available to load. Please check the session.
        </p>
      )}
      <div style={{ marginTop: "10px" }}>
        <button onClick={goToPreviousPage} disabled={currentPage === 1}>
          Previous Page
        </button>
        <button onClick={goToNextPage} disabled={currentPage === totalPages || totalPages === 0}>
          Next Page
        </button>
        <p>
          Page {currentPage} of {totalPages}
        </p>
      </div>
    </div>
  );
};

export default DocumentCanvas;
