import React, { useRef, useEffect, useCallback, useState, useMemo } from 'react';
import { useSession } from './SessionContext'; // Import the session context hook

const AudioChat = () => {
  const { sessionId } = useSession(); // Access the sessionId from the context
  const audioRef = useRef(null);
  const socket = useRef(null);
  const peerConnections = useRef({}); // Peer connections stored here
  const [isMuted, setIsMuted] = useState(false); // Mute/unmute state
  
  // Use useMemo to ensure config doesn't change unnecessarily
  const config = useMemo(() => ({
    iceServers: [{ urls: 'stun:stun.l.google.com:19302' }],
  }), []);
  
  const reconnectInterval = 5000; // Reconnection interval
  const maxReconnectAttempts = 5;
  const reconnectAttemptsRef = useRef(0); // Use ref to preserve mutable state across renders

  const handleOffer = useCallback((offer) => {
    const peerConnection = new RTCPeerConnection(config);
    peerConnections.current[offer.sender] = peerConnection;

    peerConnection.setRemoteDescription(new RTCSessionDescription(offer));
    navigator.mediaDevices.getUserMedia({ audio: true })
      .then((stream) => {
        stream.getTracks().forEach((track) => peerConnection.addTrack(track, stream));
      })
      .catch((error) => console.error('Error accessing user media:', error.message));
    
    peerConnection.createAnswer()
      .then((answer) => {
        peerConnection.setLocalDescription(answer);
        socket.current.send(
          JSON.stringify({
            type: 'answer',
            sdp: answer,
            sender: 'client',
          })
        );
      });
    
    peerConnection.ontrack = (event) => {
      audioRef.current.srcObject = event.streams[0];
    };
    peerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        socket.current.send(
          JSON.stringify({
            type: 'ice-candidate',
            candidate: event.candidate,
            sender: 'client',
          })
        );
      }
    };
  }, [config]);

  const handleAnswer = useCallback((answer) => {
    const peerConnection = peerConnections.current[answer.sender];
    if (peerConnection) {
      peerConnection.setRemoteDescription(new RTCSessionDescription(answer));
    }
  }, []);

  const handleNewICECandidateMsg = useCallback((msg) => {
    const candidate = new RTCIceCandidate(msg.candidate);
    const peerConnection = peerConnections.current[msg.sender];
    if (peerConnection) {
      peerConnection.addIceCandidate(candidate);
    }
  }, []);

  const connectWebSocket = useCallback(() => {
    if (!sessionId) {
      console.error('Session ID is undefined. Unable to initialize WebSocket.');
      return;
    }

    socket.current = new WebSocket(`ws://127.0.0.1:8080/ws/session/${sessionId}/`);

    socket.current.onopen = () => {
      console.log('WebSocket connection established');
      reconnectAttemptsRef.current = 0; // Reset reconnect attempts
      socket.current.send(JSON.stringify({ type: 'join', sessionId }));
    };

    socket.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.type === 'offer') handleOffer(data);
      if (data.type === 'answer') handleAnswer(data);
      if (data.type === 'ice-candidate') handleNewICECandidateMsg(data);
    };

    socket.current.onclose = () => {
      console.warn('WebSocket disconnected.');
      if (reconnectAttemptsRef.current < maxReconnectAttempts) {
        reconnectAttemptsRef.current += 1;
        console.warn(`Attempting to reconnect (${reconnectAttemptsRef.current}/${maxReconnectAttempts})...`);
        setTimeout(connectWebSocket, reconnectInterval);
      } else {
        console.error('Max reconnect attempts reached. Unable to reconnect WebSocket.');
      }
    };

    socket.current.onerror = (error) => {
      console.error('WebSocket error:', error.message);
    };
  }, [sessionId, handleOffer, handleAnswer, handleNewICECandidateMsg]);

  useEffect(() => {
    connectWebSocket();

    const localPeerConnections = peerConnections.current; // Capture current state

    return () => {
      socket.current?.close();
      Object.values(localPeerConnections).forEach((peerConnection) => peerConnection.close());
    };
  }, [connectWebSocket]);

  const toggleMute = () => {
    const audioTracks = audioRef.current.srcObject?.getAudioTracks();
    if (audioTracks) {
      audioTracks.forEach((track) => (track.enabled = !isMuted));
      setIsMuted(!isMuted);
    }
  };

  return (
    <div>
      <audio ref={audioRef} autoPlay />
      <button onClick={toggleMute}>{isMuted ? 'Unmute' : 'Mute'}</button>
    </div>
  );
};

export default AudioChat;
