import React, { useState, useRef, useEffect } from 'react';
import { Canvas, Image, util } from 'fabric';

const DocumentCanvas = ({ documentUrl, sessionId }) => {
  const canvasRef = useRef(null);
  const [canvas, setCanvas] = useState(null); // Keeps track of the Fabric.js canvas instance
  const socket = useRef(null);
  const reconnectAttemptsRef = useRef(0); // Preserve reconnect attempts across renders
  const reconnectInterval = 5000; // Reconnection interval (in milliseconds)
  const maxReconnectAttempts = 5; // Limit retry attempts

  useEffect(() => {
    console.log('Initializing canvas...');
    const canvasInstance = new Canvas(canvasRef.current, {
      width: 800,
      height: 600,
    });
    setCanvas(canvasInstance);

    // Load the background image
    if (documentUrl) {
      Image.fromURL(
        documentUrl,
        (img) => {
          canvasInstance.setBackgroundImage(
            img,
            canvasInstance.renderAll.bind(canvasInstance),
            {
              crossOrigin: 'anonymous', // Prevent cross-origin issues
            }
          );
          console.log('Document URL loaded:', documentUrl);
        },
        { crossOrigin: 'anonymous' }
      );
    } else {
      console.warn('No document URL provided.');
    }

    const connectWebSocket = () => {
      if (!sessionId) {
        console.error('Session ID is undefined. Unable to initialize WebSocket.');
        return;
      }

      socket.current = new WebSocket(`ws://127.0.0.1:8080/ws/session/${sessionId}/`);

      socket.current.onopen = () => {
        console.log('WebSocket connection established.');
        reconnectAttemptsRef.current = 0; // Reset reconnect attempts
      };

      socket.current.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === 'draw') {
          console.log('Received draw data:', data.payload);
          util.enlivenObjects([data.payload], (objects) => {
            objects.forEach((obj) => {
              if (canvasInstance) {
                canvasInstance.add(obj);
              }
            });
          });
        }
      };

      socket.current.onclose = () => {
        console.warn('WebSocket disconnected.');
        if (reconnectAttemptsRef.current < maxReconnectAttempts) {
          reconnectAttemptsRef.current += 1;
          console.warn(`Attempting to reconnect (${reconnectAttemptsRef.current}/${maxReconnectAttempts})...`);
          setTimeout(connectWebSocket, reconnectInterval);
        } else {
          console.error('Max reconnect attempts reached. Unable to reconnect WebSocket.');
        }
      };

      socket.current.onerror = (error) => {
        console.error('WebSocket error:', error.message);
      };
    };

    connectWebSocket();

    canvasInstance.isDrawingMode = true;
    canvasInstance.on('path:created', (e) => {
      const path = e.path;
      emitObjectData(path);
    });

    const emitObjectData = (object) => {
      try {
        if (socket.current) {
          socket.current.send(
            JSON.stringify({
              type: 'draw',
              payload: object.toJSON(),
            })
          );
          console.log('Object emitted:', object);
        }
      } catch (error) {
        console.error('Error emitting object data:', error.message);
      }
    };

    return () => {
      console.log('Cleaning up resources...');
      canvasInstance.dispose();
      if (socket.current) {
        socket.current.close();
      }
    };
  }, [documentUrl, sessionId]);

  return (
    <div>
      <canvas ref={canvasRef} />
      {!documentUrl && <p style={{ color: 'red' }}>No document available to load.</p>}
    </div>
  );
};

export default DocumentCanvas;
