import React, { useState, useEffect } from 'react';
import DocumentCanvas from './DocumentCanvas';
import AudioChat from './AudioChat';

const SessionPage = ({ documentUrl, sessionId }) => {
  const [uploadedDocument, setUploadedDocument] = useState(null);

  const handleDocumentUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setUploadedDocument(e.target.result);
        console.log('Document uploaded:', e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  useEffect(() => {
    console.log('SessionPage rendered with sessionId:', sessionId);
  }, [sessionId]);

  return (
    <div>
      <h1>Session Page</h1>
      <input type="file" onChange={handleDocumentUpload} />
      <DocumentCanvas documentUrl={uploadedDocument || documentUrl} />
      <AudioChat sessionId={sessionId} />
    </div>
  );
};

export default SessionPage;
