import React, { useState, useRef, useLayoutEffect } from "react";
import * as pdfjs from "pdfjs-dist";
import { useSession } from "./SessionContext"; // Import the session context hook
const fabric = require("fabric").fabric; // Proper ES module import for Fabric.js
// Set the worker source for pdf.js
pdfjs.GlobalWorkerOptions.workerSrc = "/pdf.worker.mjs"; // Host worker in public/



const DocumentCanvas = () => {
  const { sessionId, documentUrl } = useSession(); // Access session data
  const canvasRef = useRef(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);

  useLayoutEffect(() => {
    console.log("Initializing the canvas...");

    if (!canvasRef.current) {
      console.warn("Canvas reference is not ready yet.");
      return;
    }

    let canvasInstance;
    try {
      // Initialize Fabric.js canvas
      canvasInstance = new fabric.Canvas(canvasRef.current, {
        width: 800,
        height: 600,
      });
      console.log("Fabric.js canvas initialized.");
    } catch (error) {
      console.error("Error initializing Fabric.js:", error);
      return; // Exit early if initialization fails
    }

    // Function to render PDF to Fabric.js canvas
    const renderPDFToCanvas = async (pageNumber) => {
      if (!documentUrl) {
        console.warn("No document URL provided.");
        return;
      }

      try {
        const pdf = await pdfjs.getDocument(documentUrl).promise; // Load PDF
        setTotalPages(pdf.numPages); // Set total page count

        const page = await pdf.getPage(pageNumber); // Get specific page
        const viewport = page.getViewport({ scale: 1.5 });

        // Create a separate canvas for PDF rendering
        const pdfCanvas = document.createElement("canvas");
        pdfCanvas.width = viewport.width;
        pdfCanvas.height = viewport.height;

        const context = pdfCanvas.getContext("2d");
        await page.render({ canvasContext: context, viewport }).promise;

        // Set PDF as Fabric.js background image
        fabric.Image.fromURL(pdfCanvas.toDataURL(), (oImg) => {
          canvasInstance.setBackgroundImage(oImg, canvasInstance.renderAll.bind(canvasInstance));
          console.log(`PDF page ${pageNumber} rendered as background.`);
        });
      } catch (error) {
        console.error("Error rendering PDF:", error);
      }
    };

    renderPDFToCanvas(currentPage); // Render initial page

    return () => {
      console.log("Cleaning up Fabric.js canvas...");
      canvasInstance.dispose();
    };
  }, [currentPage, documentUrl]); // Dependencies

  // Handlers for navigating pages
  const goToNextPage = () => {
    if (currentPage < totalPages) setCurrentPage((prevPage) => prevPage + 1);
  };

  const goToPreviousPage = () => {
    if (currentPage > 1) setCurrentPage((prevPage) => prevPage - 1);
  };

  return (
    <div>
      <canvas ref={canvasRef} />
      {!documentUrl && (
        <p style={{ color: "red" }}>No document URL provided. Please check the session.</p>
      )}
      <div style={{ marginTop: "10px" }}>
        <button onClick={goToPreviousPage} disabled={currentPage === 1}>
          Previous Page
        </button>
        <button onClick={goToNextPage} disabled={currentPage === totalPages || totalPages === 0}>
          Next Page
        </button>
        <p>
          Page {currentPage} of {totalPages}
        </p>
      </div>
    </div>
  );
};

export default DocumentCanvas;
