import React, { useState, useEffect } from 'react';
import DocumentCanvas from './DocumentCanvas';
import AudioChat from './AudioChat';

const SessionPage = ({ documentUrl, sessionId }) => {
  const [uploadedDocument, setUploadedDocument] = useState(null);
  const [dynamicSessionId, setDynamicSessionId] = useState(sessionId);

  // Fetch a valid session ID if it's undefined
  useEffect(() => {
    if (!sessionId) {
      console.warn('Session ID is undefined. Attempting to fetch...');
      fetch('/api/get-session-id/')
        .then((response) => response.json())
        .then((data) => {
          console.log('Fetched Session ID:', data.sessionId);
          setDynamicSessionId(data.sessionId);
        })
        .catch((error) => {
          console.error('Error fetching Session ID:', error);
        });
    }
  }, [sessionId]);

  // Handle file uploads
  const handleDocumentUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Validate file type
      if (!file.type.startsWith('application/pdf')) {
        console.warn('Invalid file type. Please upload a PDF document.');
        return;
      }

      const reader = new FileReader();
      reader.onload = (e) => {
        setUploadedDocument(e.target.result);
        console.log('Document uploaded:', e.target.result);
      };
      reader.readAsDataURL(file);
    } else {
      console.warn('No file selected for upload.');
    }
  };

  // Debug sessionId and documentUrl during rendering
  useEffect(() => {
    console.log('SessionPage rendered with sessionId:', dynamicSessionId);
    console.log('Document URL:', documentUrl);
  }, [dynamicSessionId, documentUrl]);

  // Conditional rendering for sessionId
  if (!dynamicSessionId) {
    return <p style={{ color: 'red' }}>Error: Session ID is undefined. Unable to proceed.</p>;
  }

  return (
    <div>
      <h1>Session Page</h1>
      <input type="file" onChange={handleDocumentUpload} />
      <DocumentCanvas documentUrl={uploadedDocument || documentUrl} sessionId={dynamicSessionId} />
      <AudioChat sessionId={dynamicSessionId} />
    </div>
  );
};

export default SessionPage;
