import React, { createContext, useState, useContext } from 'react';
import FetchSessionData from './FetchSessionData'; // Ensure correct path

const SessionContext = createContext();

export const SessionProvider = ({ children }) => {
  const [sessionData, setSessionData] = useState({ sessionId: null, documentUrl: null });

  const handleSessionData = (data) => {
    console.log('Setting session data:', data);
    setSessionData({
      sessionId: data.id, // Use `id` as sessionId
      documentUrl: data.document, // Use `document` for the document URL
    });
  };

  return (
    <>
      <FetchSessionData onSessionData={handleSessionData} />
      <SessionContext.Provider value={sessionData}>
        {children}
      </SessionContext.Provider>
    </>
  );
};

export const useSession = () => useContext(SessionContext);
