import React, { useState, useEffect } from 'react';
import DocumentCanvas from './DocumentCanvas';
import AudioChat from './AudioChat';

const SessionPage = ({ documentUrl, sessionId }) => {
  const [uploadedDocument, setUploadedDocument] = useState(null);
  const [error, setError] = useState('');

  const handleDocumentUpload = (event) => {
    const file = event.target.files[0];
    if (!file) {
      setError('No file selected.');
      return;
    }

    // Validate file type and size
    const validTypes = ['application/pdf', 'image/jpeg', 'image/png'];
    const maxSize = 5 * 1024 * 1024; // 5 MB
    if (!validTypes.includes(file.type)) {
      setError('Invalid file type. Please upload a PDF, JPEG, or PNG file.');
      return;
    }
    if (file.size > maxSize) {
      setError('File size exceeds the 5MB limit. Please upload a smaller file.');
      return;
    }

    // Read file and set as uploaded document
    const reader = new FileReader();
    reader.onload = (e) => {
      setUploadedDocument(e.target.result);
      setError(''); // Clear errors on successful upload
      console.log('Document uploaded:', e.target.result);
    };
    reader.readAsDataURL(file);
  };

  useEffect(() => {
    console.log('SessionPage rendered with sessionId:', sessionId);
    console.log('Document URL:', documentUrl);
  }, [sessionId, documentUrl]);

  if (!sessionId) {
    return <p style={{ color: 'red' }}>Error: Session ID is undefined. Unable to proceed.</p>;
  }

  return (
    <div>
      <h1>Session Page</h1>
      {error && <div style={{ padding: '10px', backgroundColor: 'pink', color: 'red' }}>{error}</div>}
      <input type="file" onChange={handleDocumentUpload} />
      <DocumentCanvas documentUrl={uploadedDocument || documentUrl} sessionId={sessionId} />
      <AudioChat sessionId={sessionId} />
    </div>
  );
};

export default SessionPage;
