import React, { useState, useEffect } from 'react';
import DocumentCanvas from './DocumentCanvas';
import AudioChat from './AudioChat';
import { useSession } from './SessionContext'; // Adjust the path if necessary

const SessionPage = () => {
  const { sessionId, documentUrl } = useSession(); // Use context to get session data
  const [error, setError] = useState('');
  console.log('Session ID:', sessionId);

  useEffect(() => {
    console.log('SessionPage rendered with sessionId:', sessionId);
    console.log('Document URL:', documentUrl);
  }, [sessionId, documentUrl]);

  if (!sessionId) {
    return <p style={{ color: 'red' }}>Error: Session ID is undefined. Unable to proceed.</p>;
  }

  if (!documentUrl) {
    return <p style={{ color: 'red' }}>Error: No document URL found. Unable to render the document.</p>;
  }

  return (
    <div>
      <h1>Session Page</h1>
      {error && <div style={{ padding: '10px', backgroundColor: 'pink', color: 'red' }}>{error}</div>}
      {/* Remove the input for uploading documents */}
      <DocumentCanvas documentUrl={documentUrl} sessionId={sessionId} />
      <AudioChat sessionId={sessionId} />
    </div>
  );
};

export default SessionPage;
