   */
  async _fetch(url) {
    unreachable("Abstract method `_fetch` called.");
  }
}

class DOMStandardFontDataFactory extends BaseStandardFontDataFactory {
  /**
   * @ignore
   */
  async _fetch(url) {
    const data = await fetchData(url, /* type = */ "arraybuffer");
    return new Uint8Array(data);
  }
}

