import React, { useRef, useEffect, useCallback, useState } from 'react';
import { useSession } from './SessionContext';

const AudioChat = () => {
  const { sessionId } = useSession();
  const audioRef = useRef(null);
  const socket = useRef(null);
  const [isMuted, setIsMuted] = useState(false);

  const connectWebSocket = useCallback(() => {
    if (!sessionId) {
      console.error('Session ID is undefined. Unable to initialize WebSocket.');
      return;
    }

    // Initialize WebSocket connection
    socket.current = new WebSocket(`ws://127.0.0.1:8080/ws/session/${sessionId}/`);

    socket.current.onopen = () => {
      console.log('WebSocket connection established');
    };

    socket.current.onmessage = (event) => {
      const data = JSON.parse(event.data);

      if (data.type === 'audio') {
        console.log('Audio data received:', data.payload);
        // Handle received audio data (e.g., decode and play)
        playAudio(data.payload);
      }
    };

    socket.current.onclose = () => {
      console.warn('WebSocket disconnected.');
    };

    socket.current.onerror = (error) => {
      console.error('WebSocket error:', error.message);
    };
  }, [sessionId]);

  useEffect(() => {
    connectWebSocket();

    return () => {
      socket.current?.close();
    };
  }, [connectWebSocket]);

  // Function to capture and send audio data
  const startAudioCapture = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const audioContext = new AudioContext();
      const input = audioContext.createMediaStreamSource(stream);
      const processor = audioContext.createScriptProcessor(1024, 1, 1);

      processor.onaudioprocess = (e) => {
        const audioChunk = e.inputBuffer.getChannelData(0); // Raw audio data
        if (socket.current) {
          socket.current.send(JSON.stringify({
            type: 'audio',
            payload: audioChunk,
          }));
        }
      };

      input.connect(processor);
      processor.connect(audioContext.destination);
    } catch (error) {
      console.error('Error capturing audio:', error.message);
    }
  };

  // Function to play incoming audio
  const playAudio = (audioBuffer) => {
    try {
      const audioContext = new AudioContext();
      const buffer = audioContext.createBuffer(1, audioBuffer.length, audioContext.sampleRate);
      buffer.copyToChannel(audioBuffer, 0);

      const source = audioContext.createBufferSource();
      source.buffer = buffer;
      source.connect(audioContext.destination);
      source.start();
    } catch (error) {
      console.error('Error playing audio:', error.message);
    }
  };

  const toggleMute = () => {
    if (audioRef.current?.srcObject) {
      const audioTracks = audioRef.current.srcObject.getAudioTracks();
      audioTracks.forEach((track) => (track.enabled = !isMuted));
      setIsMuted(!isMuted);
    }
  };

  return (
    <div>
      <audio ref={audioRef} autoPlay />
      <button onClick={toggleMute}>{isMuted ? 'Unmute' : 'Mute'}</button>
      <button onClick={startAudioCapture}>Start Audio</button>
    </div>
  );
};

export default AudioChat;
