import React, { useState, useRef, useEffect } from "react";
import  "fabric"; // Correctly import fabric.js
import * as pdfjs from "pdfjs-dist";
import { useSession } from "./SessionContext"; // Import the session context hook

// Set the worker source for pdf.js
pdfjs.GlobalWorkerOptions.workerSrc =
  "https://cdnjs.cloudflare.com/ajax/libs/pdf.js/2.14.305/pdf.worker.min.js";

const DocumentCanvas = () => {
  const { sessionId, documentUrl } = useSession(); // Access the sessionId from the context
  const canvasRef = useRef(null);
  const [canvas, setCanvas] = useState(null); // Tracks the Fabric.js canvas instance
  const socket = useRef(null);
  const [currentPage, setCurrentPage] = useState(1); // Tracks the current PDF page
  const [totalPages, setTotalPages] = useState(0); // Tracks the total number of pages in the PDF
  const reconnectAttemptsRef = useRef(0); // Tracks reconnect attempts
  const reconnectInterval = 5000; // Reconnection interval (milliseconds)
  const maxReconnectAttempts = 5; // Max number of retry attempts

  useEffect(() => {
    console.log("Initializing the canvas...");
    const canvasInstance = new fabric.Canvas(canvasRef.current, {
      width: 800,
      height: 600,
    });
    setCanvas(canvasInstance);

    const renderPDFToCanvas = async (pageNumber) => {
      if (documentUrl) {
        try {
          const pdf = await pdfjs.getDocument(documentUrl).promise; // Load PDF
          setTotalPages(pdf.numPages); // Set the total number of pages

          const page = await pdf.getPage(pageNumber); // Load the specified page
          const viewport = page.getViewport({ scale: 1.5 }); // Adjust scale as needed

          const pdfCanvas = document.createElement("canvas");
          pdfCanvas.width = viewport.width;
          pdfCanvas.height = viewport.height;

          const context = pdfCanvas.getContext("2d");
          await page.render({ canvasContext: context, viewport }).promise;

          // Convert PDF canvas to an image and set as background
          fabric.Image.fromURL(pdfCanvas.toDataURL(), (oImg) => {
            canvasInstance.setBackgroundImage(
              oImg,
              canvasInstance.renderAll.bind(canvasInstance)
            );
            console.log(`PDF page ${pageNumber} rendered as background image.`);
          });
        } catch (error) {
          console.error("Error rendering PDF:", error);
        }
      } else {
        console.warn("No document URL provided.");
      }
    };

    renderPDFToCanvas(currentPage);

    const connectWebSocket = () => {
      if (!sessionId) {
        console.error(
          "Session ID is undefined. Unable to initialize WebSocket."
        );
        return;
      }

      const webSocketUrl = `ws://127.0.0.1:8080/ws/session/${sessionId}/`;
      console.log("Attempting WebSocket connection to:", webSocketUrl);

      socket.current = new WebSocket(webSocketUrl);

      socket.current.onopen = () => {
        console.log("WebSocket connection established successfully.");
        reconnectAttemptsRef.current = 0; // Reset reconnect attempts
      };

      socket.current.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === "draw") {
          console.log("Received drawing data:", data.payload);
          fabric.util.enlivenObjects([data.payload], (objects) => {
            objects.forEach((obj) => {
              if (canvasInstance) {
                canvasInstance.add(obj);
              }
            });
          });
        }
      };

      socket.current.onclose = () => {
        console.warn("WebSocket connection closed.");
        if (reconnectAttemptsRef.current < maxReconnectAttempts) {
          reconnectAttemptsRef.current += 1;
          console.warn(
            `Reconnecting... (${reconnectAttemptsRef.current}/${maxReconnectAttempts})`
          );
          setTimeout(connectWebSocket, reconnectInterval);
        } else {
          console.error(
            "Max reconnection attempts reached. Unable to reconnect."
          );
        }
      };

      socket.current.onerror = (error) => {
        console.error("WebSocket encountered an error:", error.message);
      };
    };

    // Establish WebSocket connection
    connectWebSocket();

    // Enable drawing mode
    canvasInstance.isDrawingMode = true;

    // Handle drawing events
    canvasInstance.on("path:created", (e) => {
      const path = e.path;
      emitObjectData(path);
    });

    // Emit data for WebSocket transmission
    const emitObjectData = (object) => {
      try {
        if (socket.current && socket.current.readyState === WebSocket.OPEN) {
          socket.current.send(
            JSON.stringify({
              type: "draw",
              payload: object.toJSON(),
            })
          );
          console.log("Emitted object data:", object);
        }
      } catch (error) {
        console.error("Error emitting object data:", error.message);
      }
    };

    // Cleanup on component unmount
    return () => {
      console.log("Cleaning up canvas and WebSocket...");
      if (canvasInstance) {
        canvasInstance.dispose();
      }
      if (socket.current) {
        socket.current.close();
      }
    };
  }, [documentUrl, sessionId, currentPage]); // Reinitialize if documentUrl, sessionId, or currentPage changes

  const goToNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  const goToPreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };

  return (
    <div>
      <canvas ref={canvasRef} />
      {!documentUrl && (
        <p style={{ color: "red" }}>
          No document available to load. Please check the session data.
        </p>
      )}
      <div style={{ marginTop: "10px" }}>
        <button onClick={goToPreviousPage} disabled={currentPage === 1}>
          Previous Page
        </button>
        <button
          onClick={goToNextPage}
          disabled={currentPage === totalPages || totalPages === 0}
        >
          Next Page
        </button>
        <p>
          Page {currentPage} of {totalPages}
        </p>
      </div>
    </div>
  );
};

export default DocumentCanvas;
